import java.util.Arrays;public class QuickSort {    public static void main(String[] args) {        int[] arr={88,55,11,66,44,11,22,99};        System.out.println("UnSorted :"+ Arrays.toString(arr));        qs(arr,0,arr.length-1);        System.out.println("Sorted :"+Arrays.toString(arr));    }    private static void qs(int[] arr, int low, int high) {        if (low <= high) {            int pivot= Partition(arr,low ,high);            qs(arr,low,pivot-1);            qs(arr,pivot+1,high);        }    }    private static int Partition(int[] arr, int low, int high) {        int pivot = arr[low];        int i = low + 1;        int j = high;        while (i <= j) {            while (i <= j && arr[i] <= pivot) {                i++;            }            while (i <= j && arr[j] > pivot) {                j--;            }            if (i < j) {                swap(arr, i, j);            }        }        swap(arr, low, j);        return j;    }    static  void swap(int[] arr ,int start,int end){        int temp=arr[start];        arr[start]=arr[end];        arr[end]=temp;    }}